mapper.select:
SELECT b.shop_id, b.customer_id, c.name AS customer_name, c.phone_number AS customer_phone_number, b.booking_datetime, sm.id AS shop_menu_id, sm.name AS service_name, h.id AS hairdresser_id, h.nickname AS hairdresser_name, b.progress, b.booking_way, b.memo, b.service_expect_begin_datetime, b.service_expect_end_datetime FROM booking b INNER JOIN customer c ON b.customer_id = c.id INNER JOIN hairdresser h ON b.hairdresser_id = h.id LEFT OUTER JOIN shop_menu sm ON b.shop_menu_id = sm.id WHERE b.shop_id = #{shopId}
AND b.progress = #{progress}
AND b.booking_datetime BETWEEN #{fromDateTime} AND #{toDateTime} ORDER BY b.booking_datetime DESC
